jQuery(document).ready(function(){
	 //alert("bye");

	// Disable input fields if "No" selected
	var getSavedVal  = jQuery('input[name=admin_customization_smtp_enable]:checked').val();

	if(getSavedVal == 'no') {
		// alert ();
		jQuery("#from_email_id").prop('disabled', true);
		jQuery("#from_email_name").prop('disabled', true);
		jQuery("#smtp_host").prop('disabled', true);
		jQuery("#smtp_port").prop('disabled', true);
		jQuery("#smtp_secure_SSL").prop('disabled', true);
		jQuery("#smtp_secure_TLS").prop('disabled', true);
		jQuery("#smtp_username").prop('disabled', true);
		jQuery("#smtp_password").prop('disabled', true);
	}

	jQuery('input[type=radio][name=admin_customization_smtp_enable]').change(function(){ // if user change option
		if(this.value == 'no') {
			// alert("no");
			jQuery("#from_email_id").prop('disabled', true);
			jQuery("#from_email_name").prop('disabled', true);
			jQuery("#smtp_host").prop('disabled', true);
			jQuery("#smtp_port").prop('disabled', true);
			jQuery("#smtp_secure_SSL").prop('disabled', true);
			jQuery("#smtp_secure_TLS").prop('disabled', true);
			jQuery("#smtp_username").prop('disabled', true);
			jQuery("#smtp_password").prop('disabled', true);
		} else {
			jQuery("#from_email_id").prop('disabled', false);
			jQuery("#from_email_name").prop('disabled', false);
			jQuery("#smtp_host").prop('disabled', false);
			jQuery("#smtp_port").prop('disabled', false);
			jQuery("#smtp_secure_SSL").prop('disabled', false);
			jQuery("#smtp_secure_TLS").prop('disabled', false);
			jQuery("#smtp_username").prop('disabled', false);
			jQuery("#smtp_password").prop('disabled', false);
		}
	});
	// Disable input fields if "No" selected
});
